mapper.select:
SELECT b.*, p.PRODUCT_IMAGE,
p.PRODUCT_NAME FROM bookmark b
NATURAL JOIN product p
where USER_ID =
#{userId}
order BY b.BOOKMARK_DATETIME desc;

SELECT b.*, p.PRODUCT_IMAGE,
p.PRODUCT_NAME FROM bookmark b
NATURAL JOIN product p
where USER_ID =
#{userId}
order BY p.PRODUCT_NAME;

SELECT * FROM BOOKMARK
WHERE
PRODUCT_ID =#{productId} and USER_ID = #{userId}

mapper.delete:
DELETE FROM BOOKMARK
WHERE PRODUCT_ID = #{productId}
and USER_ID = #{userId}

mapper.sql:
select b.*, p.PRODUCT_NAME from BOOKMARK b
NATURAL JOIN product p

	OR USER_ID like #{searchKeyword}
	OR PRODUCT_NAME like #{searchKeyword}

	OR COMPANY_ID IN (SELECT COMPANY_ID FROM COMPANY WHERE
		COMPANY_NAME like #{searchKeyword})
