mapper.select:
SELECT b.*, p.PRODUCT_IMAGE, p.PRODUCT_NAME FROM bookmark b
NATURAL JOIN product p
order BY b.BOOKMARK_DATETIME desc;

SELECT b.*, p.PRODUCT_IMAGE, p.PRODUCT_NAME FROM bookmark b
NATURAL JOIN product p
order BY p.PRODUCT_NAME;

mapper.update:
UPDATE USER
SET BOOKMARK_ID=#{bookmarkId}, 
	USER_ID=#{userId},
	BOOKMARK_DATETIME=#{bookmarkDatetime},
	PRODUCT_ID=#{productId}
WHERE BOOKMARK_ID=#{bookmarkId}

mapper.insert:
INSERT INTO BOOKMARK
VALUES

	(
	(SELECT IFNULL(MAX(BOOKMARK_ID) + (1+#{index}),  (1+#{index})) FROM BOOKMARK B where user_id = #{item.userId})
	,#{item.userId}
	,#{item.bookmarkDatetime}
	,#{item.productId}
	)
