mapper.sql:
select * from HISTORY

	OR HISTORY_ID like #{searchKeyword}
	OR USER_ID like #{searchKeyword}
	OR PRODUCT_ID IN (SELECT PRODUCT_ID FROM PRODUCT WHERE PRODUCT_NAME like #{searchKeyword})

mapper.insert:
INSERT INTO HISTORY
VALUES

	(
	(SELECT IFNULL(MAX(HISTORY_ID) + (1+#{index}),  (1+#{index})) FROM HISTORY B where user_id = #{item.userId})
	,#{item.userId}
	,#{item.productId}
	,#{item.historyDatetime}
	,#{item.historyComment}
	)
