mapper.update:

	select (select PLANT_IMAGE
	from
	PLANT_LEVEL P
	WHERE
	P.PLANT_LEVEL_ID = #{stateId}
	AND P.PLANT_ID = U.PLANT_ID)
	PLANT
	from
	USER U
	where U.USER_ID = #{userId}

update USER
set STATE_ID = #{stateId}
where USER_ID = #{userId}

mapper.sql:
select * from user

	OR USER_ID like #{searchKeyword}
	OR USER_NICK like #{searchKeyword}
	OR USER_GENDER like #{searchKeyword}
	OR STATE_ID IN (SELECT STATE_ID FROM STATE WHERE STATE_NAME
		like #{searchKeyword})

	OR FOREST_ID IN (SELECT FOREST_ID FROM FOREST WHERE FOREST_NAME
		like #{searchKeyword})

	OR PLANT_ID IN (SELECT PLANT_ID FROM PLANT WHERE PLANT_NAME
		like #{searchKeyword})

mapper.delete:
DELETE FROM USER
WHERE USER_ID in (

	#{item.userId}

)
